package com.org;

public class ControlProgram4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=200;
		int c=300;
		if((a>b)&&(a>c))
		{
		System.out.println("A is big");
		}
		else if((b>a)&&(b>c))
		{
		System.out.println("B is big");
		}
		else
		{
		System.out.println("C is big");
		}
	}

}
